import React, { useState } from 'react';
import styled from 'styled-components';
import ChatContainer from './components/ChatContainer';
import InputBox from './components/InputBox';
import ChatBubble from './components/ChatBubble'; // Import ChatBubble

const AppContainer = styled.div`
display: flex;
flex-direction: column;
align-items: center;
justify-content: center;
height: 100vh;
background-color: #e9ecef;
width: 100%;  // Ensure the container takes full width
`;

const App = () => {
    const questions = [
        { question: 'What is the capital of France?', answer: 'Paris' },
        { question: 'What is 2 + 2?', answer: '4' },
        { question: 'What is the capital of Spain?', answer: 'Madrid' }
    ];

    const [messages, setMessages] = useState([
        { text: questions[0].question, isUser: false }
    ]);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [score, setScore] = useState(0);

    const handleSend = (message) => {
        const newMessages = [
            ...messages,
            { text: message, isUser: true }
        ];

        const isCorrect = message.trim().toLowerCase() === questions[currentQuestionIndex].answer.trim().toLowerCase();

        if (isCorrect) {
            setScore(score + 1);
            newMessages.push({ text: 'Correct!', isUser: false });
        } else {
            newMessages.push({ text: `Incorrect. The correct answer is ${questions[currentQuestionIndex].answer}.`, isUser: false });
        }

        setMessages(newMessages);
        handleNextQuestion(newMessages, currentQuestionIndex + 1, isCorrect);
    };

    const handleNextQuestion = (newMessages, nextQuestionIndex, isCorrect) => {
        setTimeout(() => {
            if (nextQuestionIndex < questions.length) {
                setMessages([
                    ...newMessages,
                    { text: questions[nextQuestionIndex].question, isUser: false }
                ]);
                setCurrentQuestionIndex(nextQuestionIndex);
            } else {
                setMessages([
                    ...newMessages,
                    { text: `Quiz finished! Your score is ${score + (isCorrect ? 1 : 0)}/${questions.length}.`, isUser: false }
                ]);
            }
        }, 2500); // Adjust the delay if necessary
    };

    return (
        <AppContainer>
        <ChatContainer messages={messages} />
        <InputBox onSend={handleSend} />
        </AppContainer>
    );
};

export default App;
