import React from 'react';
import styled from 'styled-components';

const Bubble = styled.button`
background-color: #007bff;
color: #fff;
padding: 10px 20px;
border-radius: 20px;
margin: 10px 5px;
border: none;
cursor: pointer;
font-size: 16px;
&:hover {
    background-color: #0056b3;
}
&:disabled {
    background-color: #c0c0c0;
    cursor: not-allowed;
}
`;

const OptionBubble = ({ option, onClick, disabled }) => {
    return <Bubble onClick={() => onClick(option)} disabled={disabled}>{option}</Bubble>;
};

export default OptionBubble;
