import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import ChatContainer from './components/ChatContainer';
import quizConfig from './quiz_config.json'; // Import the quiz config

const AppContainer = styled.div`
display: flex;
flex-direction: column;
align-items: center;
justify-content: center;
height: 100vh;
background-color: #e9ecef;
width: 100%;  // Ensure the container takes full width
`;

const App = () => {
    const [questions, setQuestions] = useState([]);
    const [showCorrectOrIncorrect, setShowCorrectOrIncorrect] = useState(false);
    const [showCorrectAnswers, setShowCorrectAnswers] = useState(false);
    const [messages, setMessages] = useState([]);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [score, setScore] = useState(0);
    const [isQuizFinished, setIsQuizFinished] = useState(false);
    const [endOptions, setEndOptions] = useState([]);
    const [answerSequence, setAnswerSequence] = useState([]);

    useEffect(() => {
        // Load quiz questions and settings from JSON file
        setQuestions(quizConfig.questions);
        setShowCorrectOrIncorrect(quizConfig.showCorrectOrIncorrect || false);
        setShowCorrectAnswers(quizConfig.showCorrectAnswers || false);
        setMessages([{ text: quizConfig.questions[0].question, isUser: false }]);
    }, []);

    const handleOptionClick = (option) => {
        if (isQuizFinished) return; // Prevent option clicks if quiz is finished

        const newMessages = [
            ...messages,
            { text: option, isUser: true }
        ];

        const isCorrect = option === questions[currentQuestionIndex].answer;

        if (isCorrect) {
            setScore(score + 1);
            if (showCorrectOrIncorrect) {
                newMessages.push({ text: 'Correct!', isUser: false }); // Print correct answer
            }
        } else {
            if (showCorrectOrIncorrect) {
                newMessages.push({ text: 'Incorrect.', isUser: false }); // Print incorrect answer without revealing the correct one
            }
            if (showCorrectAnswers) {
                newMessages.push({ text: `The correct answer is ${questions[currentQuestionIndex].answer}.`, isUser: false }); // Optionally print correct answer
            }
        }

        // Store the answer in the sequence
        setAnswerSequence([...answerSequence, { question: questions[currentQuestionIndex].question, selectedOption: option, isCorrect }]);

        setMessages(newMessages);
        handleNextQuestion(newMessages, currentQuestionIndex + 1, isCorrect);
    };

    const handleNextQuestion = (newMessages, nextQuestionIndex, isCorrect) => {
        setTimeout(() => {
            if (nextQuestionIndex < questions.length) {
                setTimeout(() => {
                    setMessages([
                        ...newMessages,
                        { text: questions[nextQuestionIndex].question, isUser: false } // Print next question
                    ]);
                    setCurrentQuestionIndex(nextQuestionIndex);
                }, 2000); // Delay before printing the next question
            } else {
                setMessages([
                    ...newMessages,
                    { text: `Quiz finished! Your score is ${score + (isCorrect ? 1 : 0)}/${questions.length}.`, isUser: false }
                ]);
                setIsQuizFinished(true); // Set quiz as finished
                setEndOptions(['Yes', 'No']); // Show end options

                // Save the answer sequence to local storage
                localStorage.setItem('answerSequence', JSON.stringify([...answerSequence, { question: questions[currentQuestionIndex].question, selectedOption: isCorrect ? questions[currentQuestionIndex].answer : "Incorrect", isCorrect }]));
            }
        }, 500); // Delay after showing the answer before moving to the next question
    };

    const handleEndOptionClick = (option) => {
        if (option === 'Yes') {
            // Reset the quiz
            setMessages([{ text: questions[0].question, isUser: false }]);
            setCurrentQuestionIndex(0);
            setScore(0);
            setIsQuizFinished(false);
            setEndOptions([]);
            setAnswerSequence([]);
            localStorage.removeItem('answerSequence'); // Clear stored answers
        } else {
            // Handle "No" option if needed, e.g., close the quiz or show a message
        }
    };

    const handleDownload = () => {
        const data = localStorage.getItem('answerSequence');
        if (!data) return;

        const blob = new Blob([data], { type: 'application/json' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = 'answers.json';
        a.click();
        URL.revokeObjectURL(url);
    };

    const currentOptions = currentQuestionIndex < questions.length ? questions[currentQuestionIndex].options : [];

    return (
        <AppContainer>
        <ChatContainer
        messages={messages}
        options={isQuizFinished ? endOptions : currentOptions}
        onOptionClick={isQuizFinished ? handleEndOptionClick : handleOptionClick}
        isQuizFinished={false}
        />
        {isQuizFinished && (
            <button onClick={handleDownload}>Download Answers</button>
        )}
        </AppContainer>
    );
};

export default App;
