import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import ChatContainer from './components/ChatContainer';
import OptionBubble from './components/OptionBubble';
import quizConfig from './quiz_config.json'; // Import the quiz config

const AppContainer = styled.div`
display: flex;
flex-direction: column;
align-items: center;
justify-content: center;
height: 100vh;
background-color: #e9ecef;
width: 100%;  // Ensure the container takes full width
`;

const App = () => {
    const [questions, setQuestions] = useState([]);
    const [messages, setMessages] = useState([]);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [score, setScore] = useState(0);
    const [isQuizFinished, setIsQuizFinished] = useState(false);
    const [endOptions, setEndOptions] = useState([]);

    useEffect(() => {
        // Load quiz questions from JSON file
        setQuestions(quizConfig);
        setMessages([{ text: quizConfig[0].question, isUser: false }]);
    }, []);

    const handleOptionClick = (option) => {
        if (isQuizFinished) return; // Prevent option clicks if quiz is finished

        const newMessages = [
            ...messages,
            { text: option, isUser: true }
        ];

        const isCorrect = option === questions[currentQuestionIndex].answer;

        if (isCorrect) {
            setScore(score + 1);
            newMessages.push({ text: 'Correct!', isUser: false }); // Print correct answer
        } else {
            newMessages.push({ text: `Incorrect. The correct answer is ${questions[currentQuestionIndex].answer}.`, isUser: false }); // Print incorrect answer
        }

        setMessages(newMessages);
        handleNextQuestion(newMessages, currentQuestionIndex + 1, isCorrect);
    };

    const handleNextQuestion = (newMessages, nextQuestionIndex, isCorrect) => {
        setTimeout(() => {
            if (nextQuestionIndex < questions.length) {
                setTimeout(() => {
                    setMessages([
                        ...newMessages,
                        { text: questions[nextQuestionIndex].question, isUser: false } // Print next question
                    ]);
                    setCurrentQuestionIndex(nextQuestionIndex);
                }, 2000); // Delay before printing the next question
            } else {
                setMessages([
                    ...newMessages,
                    { text: `Quiz finished! Your score is ${score + (isCorrect ? 1 : 0)}/${questions.length}.`, isUser: false }
                ]);
                setIsQuizFinished(true); // Set quiz as finished
                setEndOptions(['Yes', 'No']); // Show end options
            }
        }, 500); // Delay after showing the answer before moving to the next question
    };

    const handleEndOptionClick = (option) => {
        if (option === 'Yes') {
            // Reset the quiz
            setMessages([{ text: questions[0].question, isUser: false }]);
            setCurrentQuestionIndex(0);
            setScore(0);
            setIsQuizFinished(false);
            setEndOptions([]);
        } else {
            // Handle "No" option if needed, e.g., close the quiz or show a message
        }
    };

    const currentOptions = currentQuestionIndex < questions.length ? questions[currentQuestionIndex].options : [];

    return (
        <AppContainer>
        <ChatContainer
        messages={messages}
        options={isQuizFinished ? endOptions : currentOptions}
        onOptionClick={isQuizFinished ? handleEndOptionClick : handleOptionClick}
        isQuizFinished={false}
        />
        </AppContainer>
    );
};

export default App;
