import React from 'react';
import styled from 'styled-components';
import ChatBubble from './ChatBubble';
import OptionBubble from './OptionBubble';

const Container = styled.div`
display: flex;
flex-direction: column;
align-items: center; /* Center align items */
width: 100%;
max-height: 70vh;
overflow-y: auto;
padding: 20px;
border: 1px solid #ccc;
border-radius: 10px;
background-color: #fff;
box-sizing: border-box; /* Ensure padding and border are included in width */
`;

const MessagesContainer = styled.div`
width: 100%;
overflow-y: auto; /* Ensure messages are scrollable */
`;

const OptionsContainer = styled.div`
display: flex;
flex-direction: column;
align-items: center;
width: 100%;
margin-top: 10px; /* Add some space between messages and options */

@media (min-width: 600px) {
    flex-direction: row;
    justify-content: center;
}
`;

const ChatContainer = ({ messages, options, onOptionClick, isQuizFinished }) => {
    return (
        <Container>
        <MessagesContainer>
        {messages.map((message, index) => (
            <ChatBubble key={index} message={message.text} isUser={message.isUser} />
        ))}
        </MessagesContainer>
        <OptionsContainer>
        {options.map((option, index) => (
            <OptionBubble key={index} option={option} onClick={() => onOptionClick(option)} />
        ))}
        </OptionsContainer>
        </Container>
    );
};

export default ChatContainer;
