import React from 'react';
import styled from 'styled-components';
import ChatBubble from './ChatBubble';
import OptionBubble from './OptionBubble';

// Styled container for the chat interface
const Container = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 100%;
    max-height: 70vh;
    overflow-y: auto;
    padding: 20px;
    border: 1px solid #ccc;
    border-radius: 10px;
    background-color: #fff;
    box-sizing: border-box;
`;

// Styled container for the option buttons
const OptionsContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 100%;

    @media (min-width: 600px) {
        flex-direction: row;
        justify-content: center;
    }
`;

// ChatContainer component to display chat messages and options
const ChatContainer = ({ messages, options, onOptionClick }) => {
    return (
        <Container>
            {messages.map((message, index) => (
                <ChatBubble key={index} message={message.text} isUser={message.isUser} />
            ))}
            <OptionsContainer>
                {options.map((option, index) => (
                    <OptionBubble key={index} option={option} onClick={() => onOptionClick(option)} />
                ))}
            </OptionsContainer>
        </Container>
    );
};

export default ChatContainer;
