import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import ChatContainer from './components/ChatContainer';
import EmailInput from './components/EmailInput';
import Logo from './components/Logo'; // Import the Logo component
import quizConfig from './quiz_config.json'; // Import the quiz config

const AppContainer = styled.div`
display: flex;
flex-direction: column;
align-items: center;
justify-content: center;
height: 200vh;
background-color: #e9ecef;
width: 100%;  // Ensure the container takes full width
position: relative; /* Ensure the logo can be positioned correctly */
`;

const ContentContainer = styled.div`
display: flex;
flex-direction: column;
align-items: center;
justify-content: center;
width: 100%;
max-width: 600px; /* Adjust the max width as needed */
padding-top: 120px; /* Adjust padding to avoid content being hidden behind the logo */
`;

const App = () => {
    const [questions, setQuestions] = useState([]);
    const [showCorrectOrIncorrect, setShowCorrectOrIncorrect] = useState(false);
    const [showCorrectAnswers, setShowCorrectAnswers] = useState(false);
    const [messages, setMessages] = useState([]);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [score, setScore] = useState(0);
    const [isQuizFinished, setIsQuizFinished] = useState(false);
    const [endOptions, setEndOptions] = useState([]);
    const [showTryAgain, setShowTryAgain] = useState(false); // State for the "Try again?" bubble
    const [answerSequence, setAnswerSequence] = useState([]);
    const [answeredQuestions, setAnsweredQuestions] = useState([]);
    const [userEmail, setUserEmail] = useState('');
    const [isEmailSubmitted, setIsEmailSubmitted] = useState(false);

    useEffect(() => {
        // Load quiz questions and settings from JSON file
        setQuestions(quizConfig.questions);
        setShowCorrectOrIncorrect(quizConfig.showCorrectOrIncorrect || false);
        setShowCorrectAnswers(quizConfig.showCorrectAnswers || false);
    }, []);

    const handleEmailSubmit = (email) => {
        setUserEmail(email);
        setIsEmailSubmitted(true);
        setMessages([{ text: quizConfig.questions[0].question, isUser: false }]);
    };

    const handleOptionClick = (option) => {
        if (isQuizFinished || answeredQuestions.includes(currentQuestionIndex)) return; // Prevent option clicks if quiz is finished or question already answered

        const newMessages = [
            ...messages,
            { text: option, isUser: true }
        ];

        const isCorrect = option === questions[currentQuestionIndex].answer;

        if (isCorrect) {
            setScore(score + 1);
            if (showCorrectOrIncorrect) {
                newMessages.push({ text: 'Correct!', isUser: false }); // Print correct answer
            }
        } else {
            if (showCorrectOrIncorrect) {
                newMessages.push({ text: 'Incorrect.', isUser: false }); // Print incorrect answer without revealing the correct one
            }
            if (showCorrectAnswers) {
                newMessages.push({ text: `The correct answer is ${questions[currentQuestionIndex].answer}.`, isUser: false }); // Optionally print correct answer
            }
        }

        // Store the answer in the sequence and mark the question as answered
        setAnswerSequence([...answerSequence, { question: questions[currentQuestionIndex].question, selectedOption: option, isCorrect }]);
        setAnsweredQuestions([...answeredQuestions, currentQuestionIndex]);

        setMessages(newMessages);
        handleNextQuestion(newMessages, currentQuestionIndex + 1, isCorrect);
    };

    const handleNextQuestion = (newMessages, nextQuestionIndex, isCorrect) => {
        setTimeout(() => {
            if (nextQuestionIndex < questions.length) {
                setTimeout(() => {
                    setMessages([
                        ...newMessages,
                        { text: questions[nextQuestionIndex].question, isUser: false } // Print next question
                    ]);
                    setCurrentQuestionIndex(nextQuestionIndex);
                }, 2000); // Delay before printing the next question
            } else {
                setMessages([
                    ...newMessages,
                    { text: `Quiz finished! Your score is ${score + (isCorrect ? 1 : 0)}/${questions.length}.`, isUser: false }
                ]);
                setIsQuizFinished(true); // Set quiz as finished
                setEndOptions(['Yes', 'No']); // Show end options

                // Save the answer sequence to local storage
                const finalAnswerSequence = [...answerSequence, { question: questions[currentQuestionIndex].question, selectedOption: isCorrect ? questions[currentQuestionIndex].answer : "Incorrect", isCorrect }];
                localStorage.setItem('answerSequence', JSON.stringify(finalAnswerSequence));

                // Send the answer sequence to the server
                const dataToSend = {
                    email: userEmail,
                   answers: finalAnswerSequence
                };
                fetch('http://localhost:5000/save-answers', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify(dataToSend)
                })
                .then(response => response.text())
                .then(data => {
                    console.log('Success:', data);
                    setTimeout(() => setShowTryAgain(true), 500); // Delay showing "Try again?" bubble by 500ms
                })
                .catch((error) => {
                    console.error('Error:', error);
                });
            }
        }, 500); // Delay after showing the answer before moving to the next question
    };

    const handleEndOptionClick = (option) => {
        if (option === 'Yes') {
            // Reset the quiz
            setMessages([{ text: questions[0].question, isUser: false }]);
            setCurrentQuestionIndex(0);
            setScore(0);
            setIsQuizFinished(false);
            setShowTryAgain(false);
            setEndOptions([]);
            setAnswerSequence([]);
            setAnsweredQuestions([]);
            localStorage.removeItem('answerSequence'); // Clear stored answers
        } else {
            // Reset to the email entering page
            setIsEmailSubmitted(false);
            setUserEmail('');
            setMessages([]);
            setCurrentQuestionIndex(0);
            setScore(0);
            setIsQuizFinished(false);
            setShowTryAgain(false);
            setEndOptions([]);
            setAnswerSequence([]);
            setAnsweredQuestions([]);
            localStorage.removeItem('answerSequence'); // Clear stored answers
        }
    };

    const currentOptions = currentQuestionIndex < questions.length ? questions[currentQuestionIndex].options : [];

    return (
        <AppContainer>
        <Logo /> {/* Add the Logo component */}
        <ContentContainer>
        {!isEmailSubmitted ? (
            <EmailInput onEmailSubmit={handleEmailSubmit} />
        ) : (
            <ChatContainer
            messages={messages}
            options={isQuizFinished ? endOptions : currentOptions}
            onOptionClick={isQuizFinished ? handleEndOptionClick : handleOptionClick}
            isQuizFinished={isQuizFinished} // Pass the isQuizFinished state
            showTryAgain={showTryAgain} // Pass the showTryAgain state
            />
        )}
        </ContentContainer>
        </AppContainer>
    );
};

export default App;
