import React from 'react';
import styled from 'styled-components';

const BotMessageContainer = styled.div`
display: flex;
align-items: flex-start;
margin-bottom: 10px;
`;

const UserMessageContainer = styled.div`
display: flex;
justify-content: flex-end;
margin-bottom: 10px;
`;

const IconWrapper = styled.div`
width: 4px;
height: 40px;
border-radius: 50%;
overflow: hidden;
margin-right: 10px;
flex-shrink: 0;
`;

const Icon = styled.img`
width: 100%;
height: 100%;
object-fit: cover;
`;

const MessageBubble = styled.div`
background-color: ${props => (props.isUser ? '#007bff' : '#e9ecef')};
color: ${props => (props.isUser ? '#fff' : '#000')};
padding: 10px 15px;
border-radius: 15px;
max-width: 60%;
align-self: ${props => (props.isUser ? 'flex-end' : 'flex-start')}; /* Align bot message bubble to the start */
`;

const ChatBubble = ({ message, isUser }) => {
  if (isUser) {
    return (
      <UserMessageContainer>
      <MessageBubble isUser>{message}</MessageBubble>
      </UserMessageContainer>
    );
  } else {
    return (
      <BotMessageContainer>
      <IconWrapper>
      <Icon src={`${process.env.PUBLIC_URL}/favicon.ico`} alt="Bot Icon" />
      </IconWrapper>
      <MessageBubble>{message}</MessageBubble>
      </BotMessageContainer>
    );
  }
};

export default ChatBubble;
