import React, { useState } from 'react';
import styled from 'styled-components';

const EmailContainer = styled.div`
display: flex;
flex-direction: column;
align-items: center;
justify-content: center;
height: 100vh;
background-color: #e9ecef;
width: 100%;
`;

const Input = styled.input`
padding: 10px;
margin: 10px 0;
font-size: 16px;
`;

const Button = styled.button`
padding: 10px 20px;
font-size: 16px;
cursor: pointer;
background-color: #007bff;
color: white;
border: none;
border-radius: 5px;

&:hover {
    background-color: #0056b3;
}
`;

const EmailInput = ({ onEmailSubmit }) => {
    const [email, setEmail] = useState('');

    const handleSubmit = () => {
        if (email) {
            onEmailSubmit(email);
        } else {
            alert('Пожалуйста введите верную почту.');
        }
    };

    return (
        <EmailContainer>
        <h1>Нужно ли вам самое дорогое железо?</h1>
        <Input
        type="email"
        placeholder="Введите свою почту"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        />
        <Button onClick={handleSubmit}>Начать Игру</Button>
        </EmailContainer>
    );
};

export default EmailInput;
