import React from 'react';
import styled from 'styled-components';
import ChatBubble from './ChatBubble';
import OptionBubble from './OptionBubble';

// Styled container for the chat interface
const Container = styled.div`
display: flex;
flex-direction: column;
align-items: center;
width: 100%;
max-height: 70vh;
overflow-y: auto;
padding: 20px;
border: 1px solid #ccc;
border-radius: 10px;
background-color: #fff;
box-sizing: border-box;
`;

// Styled container for the option buttons
const OptionsContainer = styled.div`
display: flex;
flex-wrap: wrap; /* Allow options to wrap to the next line */
align-items: center;
justify-content: center;
width: 100%;

@media (min-width: 600px) {
    justify-content: space-between; /* Distribute space evenly between options */
}

@media (min-width: 900px) {
    flex-direction: row; /* Orient options horizontally on wider screens */
}
`;

const OptionWrapper = styled.div`
flex: 1 1 45%; /* Allow options to scale and take up 45% of the container width */
margin: 5px;

@media (min-width: 600px) {
    flex: 1 1 30%; /* Scale options to take up 30% of the container width on wider screens */
}

@media (min-width: 900px) {
    flex: 1 1 20%; /* Scale options to take up 20% of the container width on even wider screens */
}
`;

// ChatContainer component to display chat messages and options
const ChatContainer = ({ messages, options, onOptionClick }) => {
    return (
        <Container>
        {messages.map((message, index) => (
            <ChatBubble key={index} message={message.text} isUser={message.isUser} />
        ))}
        <OptionsContainer>
        {options.map((option, index) => (
            <OptionWrapper key={index}>
            <OptionBubble option={option} onClick={() => onOptionClick(option)} />
            </OptionWrapper>
        ))}
        </OptionsContainer>
        </Container>
    );
};

export default ChatContainer;
