import React from 'react';
import styled from 'styled-components';
import ChatBubble from './ChatBubble';
import OptionBubble from './OptionBubble';

const Container = styled.div`
display: flex;
flex-direction: column;
align-items: center; /* Center align items */
width: 60%;
max-height: 100vh;
overflow-y: auto;
padding: 30px;
border: 1px solid #ccc;
border-radius: 10px;
background-color: #fff;
box-sizing: border-box; /* Ensure padding and border are included in width */
`;

const OptionsContainer = styled.div`
display: flex;
flex-direction: column;
align-items: center;
width: 100%;

@media (min-width: 600px) {
    flex-direction: row;
    justify-content: center;
}
`;

const ChatContainer = ({ messages, options, onOptionClick, isQuizFinished }) => {
    return (
        <Container>
        {messages.map((message, index) => (
            <ChatBubble key={index} message={message.text} isUser={message.isUser} />
        ))}
        <OptionsContainer>
        {options.map((option, index) => (
            <OptionBubble key={index} option={option} onClick={() => onOptionClick(option)} />
        ))}
        </OptionsContainer>
        </Container>
    );
};

export default ChatContainer;
