import React from 'react';
import styled from 'styled-components';

const OptionButton = styled.button`
background-color: #007bff;
color: white;
padding: 10px;
margin: 5px;
border: none;
border-radius: 5px;
cursor: pointer;
max-width: 100%; /* Ensure it doesn't exceed the parent container */
box-sizing: border-box; /* Ensure padding and border are included in width */
text-align: center;
flex: 1; /* Allow the button to grow and shrink as needed */
white-space: nowrap; /* Prevent text from wrapping */

&:hover {
    background-color: #0056b3;
}
`;

const OptionBubble = ({ option, onClick }) => (
    <OptionButton onClick={onClick}>{option}</OptionButton>
);

export default OptionBubble;
