import React from 'react';
import styled from 'styled-components';
import ChatBubble from './ChatBubble';
import OptionBubble from './OptionBubble';

const Container = styled.div`
display: flex;
flex-direction: column;
width: 60%;
max-height: 70vh;
overflow-y: auto;
padding: 20px;
border: 1px solid #ccc;
border-radius: 10px;
background-color: #fff;
`;

const ChatContainer = ({ messages, options, onOptionClick, isQuizFinished, showTryAgain }) => {
    return (
        <Container>
        {messages.map((message, index) => (
            <ChatBubble key={index} message={message.text} isUser={message.isUser} />
        ))}
        {isQuizFinished && showTryAgain && (
            <>
            <ChatBubble message="Try again?" isUser={false} />
            {options.map((option, index) => (
                <OptionBubble key={index} option={option} onClick={() => onOptionClick(option)} />
            ))}
            </>
        )}
        {!isQuizFinished && options.map((option, index) => (
            <OptionBubble key={index} option={option} onClick={() => onOptionClick(option)} />
        ))}
        </Container>
    );
};

export default ChatContainer;
